loadstring (game:HttpGet ('[10]')) (); Key: KEY-PROJECTXRELEASED _G.AutoParry = true _G.SpamParry = false _G.Range = 100 _G.InfiniteAbility = true _G.AutoOpen = true function CreateMenu() local menu = Instance.new("ScreenGui") menu.Parent = game.CoreGui menu.Name = "BladeBallMenu" local frame = Instance.new("Frame") frame.Parent = menu frame.Size = UDim2.new(0, 300, 0, 200) frame.Position = UDim2.new(0.5, -150, 0.5, -100) frame.BackgroundColor3 = Color3.new(0, 0, 0) frame.BackgroundTransparency = 0.5 local title = Instance.new("TextLabel") title.Parent = frame title.Size = UDim2.new(1, 0, 0, 50) title.Position = UDim2.new(0, 0, 0, 0) title.Text = "Blade Ball Script Menu" title.TextColor3 = Color3.new(1, 1, 1) title.TextSize = 24 title.Font = Enum.Font.SourceSansBold title.TextStrokeColor3 = Color3.new(0, 0, 0) title.TextStrokeTransparency = 0 title.BackgroundTransparency = 1 local buttons = {} local labels = {"Auto Parry", "Spam Parry", "Range", "Infinite Ability", "Auto Open"} local variables = {"AutoParry", "SpamParry", "Range", "InfiniteAbility", "AutoOpen"} local values = {_G.AutoParry, _G.SpamParry, _G.Range, _G.InfiniteAbility, _G.AutoOpen} for i = 1, 5 do local button = Instance.new("TextButton") button.Parent = frame button.Size = UDim2.new(0.5, -10, 0, 25) button.Position = UDim2.new((i - 1) % 2 * 0.5 + 0.05, 0, math.floor((i - 1) / 2) * 0.2 + 0.3, 0) button.Text = labels[i] .. ": " .. tostring(values[i]) button.TextColor3 = Color3.new(1, 1, 1) button.TextSize = 18 button.Font = Enum.Font.SourceSans button.TextStrokeColor3 = Color3.new(0, 0, 0) button.TextStrokeTransparency = 0 button.BackgroundColor3 = Color3.new(0, 0.5, 0) button.BackgroundTransparency = 0.5 button.MouseButton1Click:Connect(function() if i == 3 then _G.Range = _G.Range + 10 if _G.Range > 200 then _G.Range = 50 end else _G[variables[i]] = not _G[variables[i]] end button.Text = labels[i] .. ": " .. tostring(_G[variables[i]]) end) buttons[i] = button end end function AutoParry() local ball =